// longest building name: 
// The star in front of the Bullock Texas State History Museum

// longest country name: 
// New Zealand

// longest city name:
// Washington D.C.

const buildingNameSize = (name:string) => name.length > 28 ? '24' : '36'

const lines = (name: string): [string, string] => {
  if (name.length <= 28) {
    return [name, '']
  }

  const index = name.lastIndexOf(' ', 28)
  if (index === -1) {
    return [name, '']
  }

  return [name.slice(0, index), name.slice(index + 1)]
}

